func testAccCESAppVersion_cesAppVersionBasicExample(context map[string]interface{}) string {
	return acctest.Nprintf(`
resource "google_ces_app" "my-app" {
    location     = "us"
    display_name = "tf-test-my-app%{random_suffix}"
    app_id       = "tf-test-app-id%{random_suffix}"
    time_zone_settings {   
        time_zone = "America/Los_Angeles"
    }
}
resource "google_ces_app_version" "my-app-version" {
    location       = "us"
    display_name   = "tf-test-my-app-version%{random_suffix}"
    app            = google_ces_app.my-app.name
    app_version_id = "tf-test-app-version-id%{random_suffix}"
    description    = "example-app-version"
}
`, context)
}

func testAccCheckCESAppVersionDestroyProducer(t *testing.T) func(s *terraform.State) error {
	return func(s *terraform.State) error {
		for name, rs := range s.RootModule().Resources {
			if rs.Type != "google_ces_app_version" {
				continue
			}
			if strings.HasPrefix(name, "data.") {
				continue
			}

			config := acctest.GoogleProviderConfig(t)

			url, err := tpgresource.ReplaceVarsForTest(config, rs, "{{CESBasePath}}projects/{{project}}/locations/{{location}}/apps/{{app}}/versions/{{name}}")
			if err != nil {
				return err
			}

			billingProject := ""

			if config.BillingProject != "" {
				billingProject = config.BillingProject
			}

			_, err = transport_tpg.SendRequest(transport_tpg.SendRequestOptions{
				Config:    config,
				Method:    "GET",
				Project:   billingProject,
				RawURL:    url,
				UserAgent: config.UserAgent,
			})
			if err == nil {
				return fmt.Errorf("CESAppVersion still exists at %s", url)
			}
		}

		return nil
	}
}
